import React from "react";
import Header from './Header';
import Main from "./Main";
import Footer from "./Footer";
import data from './data.json';
import SelectBeast from "./SelectBeast";
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      selectedBeast: '',
      selectedBeastImage: '',
      name: '',
      howToSort: ''
    };
  };

  handleSubmit = (event) => {
    event.preventDefault();
    let userName = event.target.name.value;
    let selected = event.target.selected.value;
    console.log(userName,selected)
    this.setState({
      name: userName,
      howToSort: selected
    })
  };

  handleOnShowModal = (title) => {
    this.setState({
      showModal: true,
      selectedBeast: title, 
      // selectedBeastImage: image_url
    });
  };

  handleOnHide = () => {
    this.setState({
      showModal: false
    });
  };


  render() {
    console.log('From State:'+ this.state.name);
    return (
      <>
        <Header />
        <form onSubmit={this.handleSubmit}>
          <label>
            <input type="text" name="name" onInput/>
          </label>
          <fieldset>
            <legend>Selected Horns</legend>
            <select name="selected">
              {/* make options = number of horns */}
              <option value="all">All</option>
              <option value="even">Even</option>
              <option value="odd">Odd</option>
            </select>
          </fieldset>
          <button type="submit">Submit</button>
        </form>
          <Main
            data={data}
            handleOnShowModal={this.handleOnShowModal}
          />
          <SelectBeast
            showModal={this.state.showModal}
            handleOnHide={this.handleOnHide}
            selectedBeast={this.state.selectedBeast}
          />
        <Footer />
      </>
    );
  }
}

export default App;
