import React from "react";
import data from "./data.json";
import './HornedBeast.css';
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'

class HornedBeast extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            votes: 0
        };
    };

handleVotes = () => {
    this.setState({
        votes: this.state.votes + 1
    })
}



render () {
    return (
        <Card bg={variant.to} style={{ width: '18rem' }}>
  <Card.Body>
    <Card.Title>{this.props.title}</Card.Title>
    <Card.Text>ðŸ’–{this.state.votes} times liked.</Card.Text>
    <Card.Text>{this.props.description}</Card.Text>
  <Card.Img variant="bottom" src={this.props.image_url} onClick={this.handleVotes}/>
    <Button variant="primary" onClick={this.handleVotes}> Vote Here</Button>
  </Card.Body>
</Card>
    )
}



}

export default HornedBeast;
