import React from "react";
import Header from './Header';
import Main from "./Main";
import Footer from "./Footer";
import data from './data.json';
import SelectBeast from "./SelectBeast";
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      selectedBeast: '',
      selectedBeastImage: '',
      name: '',
      howToSort: '',
      beast: data
    };
  };



  handleOnShowModal = (title) => {
    this.setState({
      showModal: true,
      selectedBeast: title, 
      // selectedBeastImage: image_url
    });
  };

  handleOnHide = () => {
    this.setState({
      showModal: false
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    let userName = event.target.name.value;
    let selected = event.target.selected.value;
// console.log(userName,selected)
    this.setState({
      name: userName,
      howToSort: selected
    });
// console.log('From State called in the submit handler: ' + this.state.name + ' ' + this.state.howToSort)
  };

  handleInput = (event) => {
    let userName = event.target.value;
// console.log(userName)
    this.setState({
      name: userName,
    });
  }

  handleSelect= (event) => {
    let selected = event.target.value;
    console.log(selected);
    if (selected === 'one') {
      let newBeasts = data.beast.filter(horns => horns === 1);
      this.setState({
        sortedBeasts : newBeasts
      })
    } else if (selected === 'two') { 
      let newBeasts = data.beast.filter(horns => horns === 2);
      this.setState({
        sortedBeasts : newBeasts
      })
    } else if (selected === 'three') { 

    } else {

    }
  }

  render() {
// console.log('From State called in the render:'+ this.state.name);
    // let 
      return (
      <>
        <Header />
        <form onSubmit={this.handleSubmit}>
          <label>
            <input type="text" name="name" onInput={this.handleInput}/>
          </label>
          <fieldset>
            <legend>Selected Horns</legend>
            <select name="selected" onChange={this.handleSelect}>
              {/* make options = number of horns */}
              <option value="one">One</option>
              <option value="two">Two</option>
              <option value="three">Three</option>
              <option value="one-hundred">One Hundred</option>
            </select>
          </fieldset>
          <button type="submit">Submit</button>
        </form>
          <Main
            data={data}
            handleOnShowModal={this.handleOnShowModal}
          />
          <SelectBeast
            showModal={this.state.showModal}
            handleOnHide={this.handleOnHide}
            selectedBeast={this.state.selectedBeast}
          />
        <Footer />
      </>
    );
  }
}

export default App;
