import React from "react";
import Modal from 'react-bootstrap/Modal'
import data from './data.json';

class SelectBeast extends React.Component {
    constructor(props, handleOnHide) {
        super(props);
        this.state = {
            showModal: false,
            selectedBeast: '',
            selectedBeastImg: ''
        };
    };

    handleOnShowModal = (title, image_url) => {
        this.setState({
            showModal: true,
            selectedBeast: title,
            selectedBeastImg: image_url
        });
    };

    render() {
        return (
            <>
                <Modal
                    show={this.state.showModal}
                    onHide={this.handleOnHide}
                >
                    <Modal.Header closeButton>
                        <Modal.Title>{this.state.selectedBeast}</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <img className='img-fluid' src={this.state.selectedBeastImg} alt={this.state.selectedBeast} /></Modal.Body>
                </Modal>
            </>
        );
    }
}


export default SelectBeast;
